import React, { useMemo } from "react";
import { animated, Keyframes } from "react-spring/renderprops";
import { useSelector } from "react-redux";
import "./LandingPage.css";

const SplashScreen = (props) => {
  const { initialScreenHeight, currentScreenHeight } = props;

  const splashScreenComplete = useSelector(
    (state) => state.splashScreenComplete.splashScreenComplete
  );

  const GlowChain = Keyframes.Spring({
    glowAnimation: [
      {
        opacity: 0.2,
        fill: "rgba(44, 44, 52, 0.8)",
        fillOpacity: "1",
        stdDeviation: "0",
        config: { duration: 200 },
      },
      {
        opacity: 1,
        fill: "rgb(44, 44, 52)",
        fillOpacity: "1",
        stdDeviation: "0",
        config: { duration: 500 },
      },
      {
        opacity: 0.5,
        fill: "rgb(44, 44, 52)",
        fillOpacity: "0",
        stdDeviation: "0",
        config: { duration: 500 },
      },
      {
        opacity: 0,
        fill: "rgb(44, 44, 52)",
        fillOpacity: "0",
        stdDeviation: "0",
        config: { duration: 500 },
      },
    ],
  });

  const renderGlowLabsLogo = useMemo(() => {
    if (!splashScreenComplete) {
      return (
        <GlowChain state="glowAnimation">
          {(styles) => (
            <>
              <g
                transform={
                  !currentScreenHeight
                    ? initialScreenHeight >= 1350
                      ? `translate(-2 -${initialScreenHeight / 19})`
                      : initialScreenHeight >= 1300
                      ? `translate(-2 -${initialScreenHeight / 15})`
                      : initialScreenHeight >= 1000
                      ? `translate(-2 -${initialScreenHeight / 12})`
                      : initialScreenHeight >= 850
                      ? `translate(-2 -${initialScreenHeight / 15})`
                      : initialScreenHeight >= 830
                      ? `translate(-2 -${initialScreenHeight / 10})`
                      : initialScreenHeight >= 768
                      ? `translate(-2 -${initialScreenHeight / 5})`
                      : `translate(-2 -${initialScreenHeight / 4})`
                    : currentScreenHeight >= 1350
                    ? `translate(-2 -${initialScreenHeight / 19})`
                    : currentScreenHeight >= 1300
                    ? `translate(-2 -${initialScreenHeight / 15})`
                    : currentScreenHeight >= 1000
                    ? `translate(-2 -${currentScreenHeight / 12})`
                    : currentScreenHeight >= 850
                    ? `translate(-2 -${currentScreenHeight / 15})`
                    : currentScreenHeight >= 830
                    ? `translate(-2 -${initialScreenHeight / 10})`
                    : currentScreenHeight >= 768
                    ? `translate(-2 -${currentScreenHeight / 5})`
                    : `translate(-2 -${currentScreenHeight / 4})`
                }
              >
                <filter
                  id="svg_glow"
                  x="-20%"
                  y="-20%"
                  width="200%"
                  height="200%"
                >
                  <feGaussianBlur
                    in="SourceGraphic"
                    stdDeviation={`${styles.stdDeviation}`}
                  />
                </filter>
                <animated.path
                  strokeLinecap="round"
                  fill={`${styles.fill}`}
                  fillOpacity={`${styles.fillOpacity}`}
                  opacity={`${styles.opacity}`}
                  filter="url(#svg_glow)"
                              d="M110 283 c0 -10 -18 -28 -40 -41 -29 -16 -40 -29 -40 -46 0 -13 -7
-26 -15 -30 -10 -3 -15 -19 -15 -45 0 -31 4 -41 20 -46 11 -3 25 -15 30 -26 7
-11 21 -19 37 -19 14 0 32 -8 39 -17 12 -16 13 -15 14 11 0 23 3 27 18 21 42
-18 53 -17 79 9 14 14 34 26 44 26 15 0 19 8 19 39 0 33 -4 40 -22 43 -17 2
-24 11 -26 30 -2 19 -15 34 -40 50 -41 24 -53 38 -46 50 3 4 -9 8 -25 8 -23 0
-31 -5 -31 -17z m64 -59 c9 -4 21 -15 26 -25 9 -15 4 -21 -24 -34 -32 -15 -36
-15 -59 -1 -13 9 -32 14 -41 10 -9 -3 -16 -2 -16 2 1 20 28 43 55 47 17 2 33
5 37 6 3 0 14 -2 22 -5z m-6 -131 c-2 -10 -13 -19 -26 -21 -17 -3 -22 2 -22
17 0 16 6 21 26 21 19 0 25 -5 22 -17z"
                />
               
              </g>
            </>
          )}
        </GlowChain>
      );
    }
  }, [splashScreenComplete, currentScreenHeight, initialScreenHeight]);

  return (
    <svg className="glow_labs_logo_animation" viewBox="0 0 463.021 463.021">
      {renderGlowLabsLogo}
    </svg>
  );
};

export default SplashScreen;
